import React from "react";
import "./Button.css";

const bStyles = ["btn--home", "btn--aboutme", "btn--projects", "btn--resume"];




const Button = ({children, type, onClick, buttonStyle}) => {
    const currS = bStyles.includes(buttonStyle) ? buttonStyle : bStyles[0];
    return (
        <button className= {`btn ${currS}`} onClick={onClick} type={type}>
            {children}
        </button>
    )
}

export default Button;
