import React from "react";
import "./Button.css";

const bStyles = ["btn--home", "btn--aboutme", "btn--projects", "btn--resume"];

function expand() {
    const part = document.getElementsByClassName("btn--aboutme")[0];
    console.log(part)
    part.style.fontSize = "xx-large"
  }



const Button = ({children, type, onClick, buttonStyle}) => {
    const currS = bStyles.includes(buttonStyle) ? buttonStyle : bStyles[0];
    return (
        <button className= {`btn ${currS}`} onClick="expand()" type={type}>
            {children}
        </button>
    )
}

export default Button;
