import React from "react";
import "./Button.css";

const bStyles = ["btn--home", "btn--aboutme", "btn--projects", "btn--resume"];

function expand() {
    for (const s of document.getElementsByClassName("btn--aboutme")) {
        s.style.fontSize = "500%";
        console.log("hi");
  }
}



const Button = ({children, type, buttonStyle}) => {
    const currS = bStyles.includes(buttonStyle) ? buttonStyle : bStyles[0];
    return (
        <button className= {`btn ${currS}`} onClick={expand()} type={type}>
            {children}
        </button>
    )
}

export default Button;
