import React from "react";
import "./Button.css";
import { useHistory } from 'react-router-dom';

const bStyles = ["btn--home", "btn--aboutme", "btn--projects", "btn--resume"];




const Button = ({children, type, buttonStyle}) => {
    const currS = bStyles.includes(buttonStyle) ? buttonStyle : bStyles[0];
    const history = useHistory();
    function expand() {
        for (const s of document.getElementsByClassName("btn")){
            if (s.classList.contains(`${currS}`)){
                s.style.fontSize = "2000%";
                s.style.color = "white"
            }
            else {
                s.style.opacity = "0";
            }
        }
        for (const s of document.getElementsByClassName("peddy")){
            s.style.opacity = "0";
        }
        setTimeout(() => history.push("/aboutme"), 400);

    }
    return (
        <button className= {`btn ${currS}`} onClick={expand} type={type}>
            {children}
        </button>
    )
}

export default Button;
