import '../App.css';
import './Projects.css';
import React, {useState} from 'react';
import Button from "../Components/Button.js";
import {useSpring, animated, useTransition} from "react-spring";
import useInterval from 'use-interval';
import Pstars from '../Components/Images/Pstars';
import Pchess from '../Components/Images/Pchess';
import Footer from "../Components/Footer.js";

const slides = [Pchess, Pstars]



function Projects() {
  const spinIn = useSpring({
    config: {duration: 300},
    from: {transform: "rotateZ(360deg)", opacity: "0"},
    to: {transform: "rotateZ(0deg)", opacity: "1"},
    delay: 800
  })
  const [index, set] = useState(0)
  const item = slides[index]
  const increment = () => set(state => (state+1) % slides.length)
  useInterval(increment, 2000)

  return (
    <div className="Projects">
        <div className="projects--top">
        <animated.div style={spinIn}>
          <Button buttonStyle="home">Home</Button>
        </animated.div>
        </div>
        <h1 className="projects--header">Projects</h1>
        <div className="projects--container">
          <div key={item}/>
        </div>

    </div>
  );
}

export default Projects;
