import '../App.css';
import './Aboutme.css';
import Button from "../Components/Button.js";
import {useSpring, useTransition, animated} from "react-spring";
import Footer from "../Components/Footer.js"
import AboutmeText from '../Components/AboutmeText';
import PeddyBerk from '../Components/Images/PeddyBerk';
import {motion} from 'framer-motion';


function Aboutme() {
  const variants = {
    initial: { x: "100vw" },
    animate: { x: "0vw", 
    transition: { duration: 1.2, cubicbezier: (0.68, 0.68, 0.68) }
     }
    }
  return (
    <div className="Aboutme">
      <div className="aboutme--top">
        <Button buttonStyle="home">Home</Button>
      </div>
      <motion.h1 initial="initial" animate="animate" variants={variants} className="aboutme--header">About Me</motion.h1>
      <div className="parent--container">
        <motion.h1 initial="initial" animate="animate" variants={variants}>
          <PeddyBerk />
        </motion.h1>
        <AboutmeText />
      </div>
      <div className="footer--aboutme">
        <Footer />
      </div>
    </div>
  );
}

export default Aboutme;
